import React, { useState, useEffect } from "react";
import axios from "axios";
import InputMask from "react-input-mask";
import { Input, Button } from "../../../static/styled-components/base";
import Message from "../../utils/Message";


export default  ({ getResellers }) => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [publicPlace, setPublicPlace] = useState("");
  const [district, setDistrict] = useState("");
  const [state, setState] = useState("");
  const [country, setCountry] = useState("");
  const [number, setNumber] = useState("");
  const [visible, setVisible] = useState(false);
  const [message, setMessage] = useState("");
  const [result, setResult] = useState(false);
  const [color, setColor] = useState("red");

  return (
    <>
      <h3 className="main-title" style={{ color: "rgb(129, 22, 27)" }}>
        Adicionar novo revendedor
      </h3>
      <div>
        <Row>
          <Input
            placeholder="Nome"
            onChange={e => setName(e.target.value)}
            value={name}
            style={{ flex: 2, marginRight: "5px" }}
          />
          <Input
            placeholder="Email"
            onChange={e => setEmail(e.target.value)}
            value={email}
            style={{ flex: 2, marginRight: "5px" }}
          />
          <InputMask
            style={{ flex: 1 }}
            placeholder="Telefone"
            onChange={e => setPhone(e.target.value)}
            value={phone}
            mask="+99 99 99999-9999"
            maskChar=" "
            className="form-control"
          />
        </Row>
        <Row>
          <Input
            style={{ flex: 2 }}
            className="spaceInputs"
            placeholder="Logradouro"
            onChange={e => setPublicPlace(e.target.value)}
            value={publicPlace}
          />
          <Input
            style={{ flex: 2 }}
            className="spaceInputs"
            placeholder="Bairro"
            onChange={e => setDistrict(e.target.value)}
            value={district}
          />
          <Input
            style={{ flex: 2 }}
            className="spaceInputs"
            placeholder="Cidade"
            onChange={e => setDistrict(e.target.value)}
            value={district}
          />
          <Input
            style={{ flex: 1 }}
            className="spaceInputs"
            placeholder="Estado"
            onChange={e => setState(e.target.value)}
            value={state}
          />
          <Input
            style={{ flex: 1 }}
            className="spaceInputs"
            placeholder="País"
            onChange={e => setCountry(e.target.value)}
            value={country}
          />
          <Input
            style={{ flex: 1 }}
            placeholder="Número"
            onChange={e => setNumber(e.target.value)}
            value={number}
          />
        </Row>
      </div>
      {visible ? (
        <Message result={result} color={color} message={message} />
      ) : (
        ""
      )}
      <Button onClick={() => createReseller()}>Criar categoria</Button>
    </>
  );

  function createReseller() {
    axios
      .post(`/reseller`, {
        name,
        phone,
        email,
        address: { publicPlace, district, state, country, number }
      })
      .then(response => {
        setResult(true);
        setColor("green");
        setMessage("Revendedor criado com sucesso");
        setVisible(true);
        getResellers();
        resetForm();
      })
      .catch(err => {
        console.log("ERROR|ADDMANU => ", err);
        setResult(false);
        setMessage(err.response.data);
        setVisible(true);
      });
  }

  function resetForm() {
    setName("");
    setEmail("");
    setPhone("");
    setPublicPlace("");
    setDistrict("");
    setState("");
    setCountry("");
    setNumber("");
  }
};

import styled from "styled-components";

export const Row = styled.div`
  display: flex;
  width: 100%;
  flex-direction: row;
  justify-content: space-between;
  input {
    margin-right: 5px;
  }
`;
