import React, { useState, useEffect } from "react";
import axios from "axios";

import Template from "../../components/Template";
import { Container, Hr } from "../../components/Admin/baseStyle";
import Nav from "../../components/Admin/Nav";
import ListManufacturers from "../../components/Admin/Manufacturers/ListManufacturers";
import AddManufacturer from "../../components/Admin/Manufacturers/AddManufacturer";

function Manufacturers() {
  const [manufacturers, setManufacturers] = useState([]);
  useEffect(() => {
    function _getManufacturers() {
      getManufacturers();
    }
    _getManufacturers();
  }, [manufacturers]);
  return (
    <Template>
      <Nav />
      <Container>
        <div style={{ width: "100%" }}>
          <h1
            className="main-title"
            style={{
              textAlign: "center",
              color: "rgb(35,43,111)"
            }}
          >
            Fabricantes
          </h1>
          <Hr />
          <div style={{ marginTop: "50px", padding: "0 15px" }}>
            <ListManufacturers
              getManufacturers={getManufacturers}
              manufacturers={manufacturers}
            />
          </div>
          <div style={{ marginTop: "50px", padding: "0 15px" }}>
            {/* <AddManufacturer getManufacturers={getManufacturers} /> */}
          </div>
        </div>
      </Container>
    </Template>
  );

  function getManufacturers() {
    axios
      .get("/manufacturer")
      .then(response => {
        setManufacturers(response.data);
      })
      .catch(err => {
        setResult(false);
        setMessage(err.response.data);
        setVisible(true);
      });
  }
}

export default Manufacturers;
